import XCTest
@testable import MultiEduca


class ReadFilesTests: XCTestCase {
    
    func testReadAndParseHomeJsonFile() {
        
        let fileReader = FilesManager()
        let models = fileReader.readHomeDataFile()
        
        XCTAssertTrue(models is [HomeDataModel])
        XCTAssertEqual(models[0].id, "1")
        XCTAssertEqual(models[0].imageBase64, "")
    }
    
    
    func testReadAndParseGameContentJsonFile() {
        
        let fileManager = FilesManager()
        let model = fileManager.readGameContentDataFile()
        
        print(model as Any)
        
        XCTAssertNotNil(model)
        XCTAssertTrue(model?.first?.levels?.first?.contents?.first?.points == 1)
    }
}
